class PhotoZoomInActivity : AppCompatActivity() {
    //var dataList = ArrayList<String>()
    var dataList : ArrayList<String> = ArrayList()
    var photo_list : ArrayList<String> = ArrayList()

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_photo_zoom_in)

        setStatusBarTransparent()
        dataList = intent.getStringArrayListExtra("imageList")
        Log.v("현주", dataList.toString())

        /*dataList.add(PhotoZoomInData("data:image/jpeg;base64,"))
        dataList.add(PhotoZoomInData("http://previews.123rf.com/images/farang/farang1109/farang110900056/10665809-wooden-boat-long-exposure-shot-vertical-panorama-in-high-resolution-.jpg"))
        dataList.add(PhotoZoomInData("http://i.pinimg.com/236x/59/87/46/5987462a0b52f2f0042b863452840870--oregon-waterfalls-multnomah-falls.jpg"))
        dataList.add(PhotoZoomInData("data:image/jpeg;base64,"))
        dataList.add(PhotoZoomInData("data:image/jpeg;base64,"))
        dataList.add(PhotoZoomInData("http://afairyfit.com/file_data/afairyfitceo/2018/02/15/567801bbfe86202b4eb60edfb9d2fd3a.jpg"))
        dataList.add(PhotoZoomInData("data:image/jpeg;base64,"))
*/
        //position 받아와서 그 위치에서 채우기
        for(i in 0..dataList.size-1){
            photo_list.add(dataList[i])//.p_img)
        }

        val adapter = PhotoZoomStatePagerAdapter(photo_list)
        val vp:ViewPager = this.findViewById(R.id.vp_photo_zoom_in_act)
        vp.adapter = adapter

        val tl:TabLayout = this.findViewById(R.id.tl_photo_zoom_in_act)
        tl.setupWithViewPager(vp,true)

        btn_photo_zoom_in_act_close.setOnClickListener{
            finish()
        }
    }

    private fun setStatusBarTransparent() {
        window.addFlags(WindowManager.LayoutParams.FLAG_DRAWS_SYSTEM_BAR_BACKGROUNDS)
        window.addFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN)
        window.decorView.systemUiVisibility =
                View.SYSTEM_UI_FLAG_LAYOUT_FULLSCREEN or
                        View.SYSTEM_UI_FLAG_LIGHT_STATUS_BAR

        setWindowFlag(WindowManager.LayoutParams.FLAG_TRANSLUCENT_STATUS, false)
        if (Build.VERSION.SDK_INT >= 21) {
            window.statusBarColor = Color.TRANSPARENT
        }
    }

    private fun setWindowFlag(bits: Int, on: Boolean) {
        val win = window
        val winParams = win.attributes
        if (on) {
            winParams.flags = winParams.flags or bits
            return
        }
        winParams.flags = winParams.flags and bits.inv()
        win.attributes = winParams
    }
}